changeSet.sql:

create table if not exists patient (
    id bigint primary key,
    name varchar(255),
    surname varchar(255),
    second_name varchar(255),
    birthday date
);
create table if not exists visit (
    id bigint primary key,
    patient_id bigint,
    date_out date,
    foreign key(patient_id) references patient(id)
);
create sequence id_seq;
